import { useState } from "react";
import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCoffee } from '@fortawesome/free-solid-svg-icons';


export default function Index()
{
    const [data,setData]= useState([]);
    const [text,setText]= useState([]);
    const [deleteData,setDeleteData]= useState([]);

    const [count,setCount]= useState(0);
    const [id,setId]= useState(0);
    const submitdata=()=>{
        setData([...data,text]);
        setCount(count+1);
        setId(id+1);
    }
    const dataCollect=(e)=>{
                    setText({
                        ...text,
                        [e.target.name]: e.target.value.trim(),
                        'id':id
                    });
    }
    const handleDelete=(deleteId)=>
    {
        if(deleteData.count>0)
        {
        data.map((dataset)=>{
                setDeleteData({
                    ...deleteData,
                    ['Input']: dataset.Input,
                    ['id']:dataset.id
                });
        })
        setData([]);
        // setData([...data,deleteData]);
    }
        console.log(deleteId);
        console.log(deleteData);
        console.log(data);
    }
    return <>
    <h1 style={{textAlign:'center'}}>HELLO!</h1>
    <input type="text" name="Input" onChange={(e)=>dataCollect(e)}></input>
    <button value="submit" onClick={()=>submitdata()}>Submit</button>
    <br></br>
    <br></br>
    {
        (count !== 0)?
        (
        data.map((dataset)=>{
            return(<div key={dataset.id}>{dataset.Input} <FontAwesomeIcon icon={faCoffee} onClick={()=>handleDelete(dataset.id)}/></div>);
        })
        ):
         <h1> </h1>
    }
    </>
}
