import { useState } from "react";
import React from 'react';


export default function Index()
{
    const [data,setData]= useState("");
    const [text,setText]= useState();
    const dataCollect=(e)=>{
        if(e.target.value === "submit")
        {
                setData(text);
                // setText([]);
        }
        else
        {
            var DataTarget;
            DataTarget = text;
            DataTarget = e.target.value;    
            setText(DataTarget);
        }
    }
    return <>
    <h1 style={{textAlign:'center'}}>HELLO HARSHIT!</h1>
    <input type="text" name="Input" style={{textAlign:'center'}} onChange={(e)=>dataCollect(e)}></input>
    <button value="submit" onClick={(e)=>dataCollect(e)} style={{textAlign:'center'}}>Submit</button>
    <br></br>
    <br></br>
    <h1 style={{textAlign:'center'}}>{data}</h1>
    </>
}
